mapper.select:
select t.* from sp_pd_pp_info  t
where 1=1

  and t.WORK_MASTER_UNAME like #{workMasterUname,jdbcType=VARCHAR}


  and t.WORK_CONTENT like #{workContent,jdbcType=VARCHAR}


  and t.WORK_SITE_NAMES like #{workSiteNames,jdbcType=VARCHAR}


  and t.PLAN_CODE = #{planCode,jdbcType=VARCHAR}


  and t.PLAN_STATE = #{planState,jdbcType=INTEGER}


  and t.PLAN_BEGIN_DATE >= #{planBeginDate,jdbcType=TIMESTAMP}


  and t.PLAN_END_DATE < #{planEndDate,jdbcType=TIMESTAMP}

order by t.UPDATE_TIME desc
